const Tasks = () => {
    const [tasks, setTasks] = useState([
        [
            {
                text: 'Doctors Appointment',
                day: 'Feb 5th 2:30pm',
                reminder: 'true',
            },
            {
                text: 'Doctors Appointment',
                day: 'Feb 5th 2:30pm',
                reminder: 'true',
            },
            {
                text: 'Doctors Appointment',
                day: 'Feb 5th 2:30pm',
                reminder: 'false',
            },
        ]
    ])
    return (
        <>
           {tasks.map((task)=>(
               <h3 key={task.id}>{task.text}</h3>
           ))}
        </>
    )
}
export default Tasks
