import {useState} from 'react'

const Tasks = () => {
    const [tasks, setTasks] = useState(
        [
            {
                id: 1,
                text: 'Doctors Appointment',
                day: 'Feb 5th 2:30pm',
                reminder: 'true',
            },
            {
                id: 2,
                text: 'Meeting at school',
                day: 'Feb 5th 2:30pm',
                reminder: 'true',
            },
            {
                id: 3,
                text: 'Food Shopping',
                day: 'Feb 5th 2:30pm',
                reminder: 'false',
            },
        ]
    )

    return (
        <>
           {tasks.map((task) => (
               <h3 key={task.id}>{task.text}</h3>
           ))}
        </>
    )
}


export default Tasks
